import './App.css';
import React, { useState, useEffect } from 'react';
import Header from './modules/header';
import SearchBar from './modules/search';
import {getSongs, getPlaylistSongs} from './modules/fetch';

function App() {
  const [songs, setSongs] = useState([]);
  const [playlist, setPlaylist] = useState([]);

  async function searchAndDisplay(songTitle) {
    const results = await getSongs(songTitle);
    setSongs(results);
    updatePlaylist();
  }

  async function updatePlaylist() {
    const results = await getPlaylistSongs();
    console.log('Playlist before setState:', playlist);  // Перевірка перед оновленням стану
    setPlaylist(results);
    console.log('Playlist after setState:', results);  // Перевірка після оновлення стану
  }

  useEffect(() => {
    // Викликається один раз при завантаженні компонента для первинного завантаження плейлиста
    updatePlaylist();
  }, []);

  return (
    <div className="App">
      <div className='container'>
        <Header />
        <SearchBar onSearch={searchAndDisplay} />

        <div className="main">
          <div className="songContainer">
            <h2>Results</h2>
              <ul className='songsList'>
              {songs.map((song) =>
              <li key={song.id} className='song'>
                <div>
                  <h3>{song.name}</h3>
                  <p className='artist'>{song.artists[0].name}</p>
                </div>
                <div>
                  <button className='addPlaylist'>Add</button>
                </div>
              </li>
              )}</ul>
          </div> 
          <div className="songContainer">
            <h2>Playlist</h2>
            <ul className='songsList'> 
              {playlist.map((song) => {
                <li key={song.track.id} className='song'>
                <div>
                  <h3>{song.track.name}</h3>
                  <p className='artist'>{}</p>
                </div>
              </li>
              })}
            </ul>
          </div>
      </div>

      </div>
    </div>
  );
}

export default App;
