const clientId = '9695df66110c4070ab27fdcb07f9d022';
const clientSecret = 'eb859c7faacb4037977fed00a8d04b19';
const urlToken = 'https://accounts.spotify.com/api/token';
const headersToken = {
  'Content-Type': 'application/x-www-form-urlencoded'
}

async function getToken () {
  const body = new URLSearchParams({
    'grant_type': 'client_credentials',
    'client_id': clientId,
    'client_secret': clientSecret
  });

  try {
    const response = await fetch(urlToken, {
      method: 'POST',
      headers: headersToken,
      body: body
    });
    const data = await response.json();
    return data;
  } catch (error) {
    console.log("Error:", error);
  }
}

async function getUserID () {
  const urlToFetch = 'https://api.spotify.com/v1/me';
  const token = await getToken();  
  const headersSearch = {
    'Authorization': `${token.token_type} ${token.access_token}`
  }
  try {
    const response = await fetch(urlToFetch, {
      method: 'GET',
      headers: headersSearch
    });
    const data = await response.json();
    console.log(data)
    return data;
  } catch (error) {
    console.error('Error:', error);
    return [];
  }
}


async function getSongs (songTitle) {
  const urlSearch = 'https://api.spotify.com/v1/search?q=';
  const trackLimit = '15';
  const options = '&type=track&limit=';
  const token = await getToken();  
  const urlToFetch = `${urlSearch}${songTitle}${options}${trackLimit}`;
  const headersSearch = {
    'Authorization': `${token.token_type} ${token.access_token}`
  }
  try {
    const response = await fetch(urlToFetch, {
      method: 'GET',
      headers: headersSearch
    });
    const data = await response.json();
    return data.tracks.items;
  } catch (error) {
    console.error('Error:', error);
    return [];
  }
}

async function getPlaylist () {
  const token = await getToken(); 
  const urlToFetch = 'https://api.spotify.com/v1/users/smedjan/playlists';
  const headersSearch = {
    'Authorization': `${token.token_type} ${token.access_token}`
  }
  try {
    const response = await fetch(urlToFetch, {
      method: 'GET',
      headers: headersSearch
    });
    const data = await response.json();
    console.log(data)
    return data;
  } catch (error) {
    console.error('Error:', error);
    return [];
  }
}

getUserID();
getPlaylist();

export default getSongs;
