import logo from './logo.svg';
import './App.css';
import React, { useState, useEffect } from 'react';
import Header from './modules/header';
import SearchBar from './modules/search';
import SongList from './modules/list';
import getData from './modules/fetch';

function App() {
  const [songs, setSongs] = useState([]);
  const [initialSearch, setInitialSearch] = useState(''); // Доданий стан для початкового пошуку

  async function searchAndDisplay(songTitle) {
    const results = await getData(songTitle);
    if (results) {
      setSongs(results);
    }
  }

  useEffect(() => {
    if (initialSearch) {
      searchAndDisplay(initialSearch);
    }
  }, [initialSearch]);

  return (
    <div className="App">
      <Header />
      <SearchBar onSearch={searchAndDisplay} />
      <SongList songs={songs} />
    </div>
  );
}

export default App;
