import './App.css';
import React, { useState, useEffect } from 'react';
import Header from './modules/header';
import SearchBar from './modules/search';
import getData from './modules/fetch';

function App() {
  const [songs, setSongs] = useState([]);
  async function searchAndDisplay(songTitle) {
    const results = await getData(songTitle);
    setSongs(results);
  }

  useEffect(() => {
    console.log('Updated', songs);
  }, [songs]);

  return (
    <div className="App">
      <Header />
      <SearchBar onSearch={searchAndDisplay} />

      <div className="main">
        <div className="songList">
          <h2>Results</h2>
          <ul>
            {

            songs.map(song => (    
              <li key={song.id}>{song.name}</li>
            ))}
          </ul>
        </div> 
        <div className="songList">
          <h2>Playlist</h2>
        </div>
      </div>

    </div>
  );
}

export default App;
