import './App.css';
import React, { useState, useEffect } from 'react';
import Header from './modules/header';
import SearchBar from './modules/search';
import getSongs from './modules/fetch';

function App() {
  const [songs, setSongs] = useState([]);
  async function searchAndDisplay(songTitle) {
    const results = await getSongs(songTitle);
    setSongs(results);
  }

  useEffect(() => {
  //  console.log('Updated', songs);
  }, [songs]);

  return (
    <div className="App">
      <div className='container'>
        <Header />
        <SearchBar onSearch={searchAndDisplay} />

        <div className="main">
          <div className="songContainer">
            <h2>Results</h2>
              <ul className='songsList'>
              {songs.map((song) =>
              <li key={song.id} className='song'>
                <h3>{song.name}</h3>
                <p>{song.artists[0].name}</p>
                </li>
              )}</ul>
          </div> 
          <div className="songContainer">
            <h2>Playlist</h2>
          </div>
      </div>

      </div>
    </div>
  );
}

export default App;
